import { useState } from 'react';
import { useProfile } from '../../state/hooks/userAuth.js';
import { useForm } from '../../state/hooks/formData.js';
import { FormButton, InputControl } from '../Forms/FormsControls.jsx';
import styles from './Profile.css';

export default function Profile() {
  const [, updateProfile] = useProfile();
  const [profile, handleChange] = useForm();
  const [preview, setPreview] = useState();

  const handlePreview = (e) => {
    const target = e.target;
    const [file] = target.files;
    setPreview(URL.createObjectURL(file));
    handleChange({
      target: {
        name: target.name,
        value: file,
      },
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    updateProfile(profile);
  };

  return (
    <section className={styles.Profile}>
      <form onSubmit={handleSubmit}>
        <h1>User Profile</h1>

        <InputControl
          label="User Name"
          name="username"
          required
          placeholder="enter user name"
          value={profile.username}
          onChange={handleChange}
        />

        <InputControl
          className={styles.Avatar}
          label="Avatar"
          name="avatar"
          required
          type="file"
          onChange={handlePreview}>
          {preview && <img src={preview} alt="avatar preview"/>}
        </InputControl>

        <FormButton>Update</FormButton>
      </form>
    </section>
  );
}
